       const canvas = Canvas.createCanvas(360,100);
       const ctx = canvas.getContext('2d');
  
  const resim1 = await Canvas.loadImage('https://i.hizliresim.com/qABdtV.png')
    const resim2 = await Canvas.loadImage('https://i.hizliresim.com/nEtjFf.png')
